<?php

namespace ToDate\Condition;

require_once TODATE_BASEDIR . '/ToDate/Condition/IntersectionCondition.php';
require_once TODATE_BASEDIR . '/ToDate/Condition/AlwaysCondition.php';
require_once TODATE_BASEDIR . '/ToDate/Condition/NeverCondition.php';

/**
 * Test class for IntersectionCondition.
 * Generated by PHPUnit on 2012-02-06 at 22:18:11.
 */
class IntersectionConditionTest extends \PHPUnit_Framework_TestCase {

	/**
	 *
	 * @var \DateTime
	 */
	protected $testDate;

	public function setUp() {
		$this->testDate = new \DateTime('2012-04-07');
	}
	
	/**
	 * @covers ToDate\Condition\IntersectionCondition::evaluate
	 */
	public function testContainsFalseWithNeverOrAlways() {
		$c = new IntersectionCondition(new NeverCondition(),new AlwaysCondition());
		self::assertFalse($c->contains($this->testDate));
	}
	
	/**
	 * @covers ToDate\Condition\IntersectionCondition::evaluate
	 */
	public function testContainsTrueWithAlwaysOrAlways() {
		$c = new IntersectionCondition(new AlwaysCondition(),new AlwaysCondition());
		self::assertTrue($c->contains($this->testDate));
	}
	
	/**
	 * @covers ToDate\Condition\IntersectionCondition::evaluate
	 */
	public function testContainsFalseWithAlwaysOrNever() {
		$c = new IntersectionCondition(new AlwaysCondition(),new NeverCondition());
		self::assertFalse($c->contains($this->testDate));

	}
	
	/**
	 * @covers ToDate\Condition\IntersectionCondition::evaluate
	 * @covers ToDate\Condition\AbstractLogicCondition::contains
	 */
	public function testContainsFalseWithNeverOrNever() {
		$c = new IntersectionCondition(new NeverCondition(),new NeverCondition());
		self::assertFalse($c->contains($this->testDate));
	}
	

	/**
	 * @covers ToDate\Condition\IntersectionCondition::__toString
	 * @covers ToDate\Condition\AbstractLogicCondition::__construct
	 */
	public function testToString() {
		$c = new IntersectionCondition(new AlwaysCondition(),new NeverCondition());
		self::assertEquals('(Always) AND (Never)', (string)$c);
	}
	
}

?>
